	private static void bouncyCastleBase64() {
		byte[] encodeBytes = org.bouncycastle.util.encoders.Base64.encode(input
				.getBytes());
		System.out.println("bouncyCastleBase64 encode="
				+ new String(encodeBytes));

		byte[] decodeBytes = org.bouncycastle.util.encoders.Base64
				.decode(encodeBytes);
		System.out.println("bouncyCastleBase64 decode="
				+ new String(decodeBytes));
	}

	private static void commonsCodecBase64() {
		byte[] encodeBytes = org.apache.commons.codec.binary.Base64
				.encodeBase64(input.getBytes());

		System.out.println("commonsCodecBase64 encode="
				+ new String(encodeBytes));

		byte[] decodeBytes = org.apache.commons.codec.binary.Base64
				.decodeBase64(encodeBytes);
		System.out.println("commonsCodecBase64 decode="
				+ new String(decodeBytes));
	}

	private static void jdkBase64() {

		try {
			BASE64Encoder encoder = new BASE64Encoder();
			String encode = encoder.encode(input.getBytes());
			System.out.println("jdkBase64 encode=" + encode);

			BASE64Decoder decoder = new BASE64Decoder();
			String decode = new String(decoder.decodeBuffer(encode));
			System.out.println("jdkBase64 decode=" + decode);
		} catch (Exception e) {
			// TODO: handle exception
			e.printStackTrace();
		}

	}
